import { createRouter, createWebHashHistory } from 'vue-router'

const router = createRouter({
    history: createWebHashHistory(),
    routes: [
        { path: '/', redirect: '/home' },
        { path: '/home', component: () => import('./components/MainPage.vue') },
        { path: '/login', component: () => import('./components/LogIn.vue') },
        { path: '/register', component: () => import('./components/UserRegister.vue') },
        { path: '/record', component: () => import('./components/LiveRecord.vue') },
        {
            path: '/admin',
            component: () => import('./components/admin/AdminLogin.vue'),
            children: [
                // 修正子路由路径（移除开头的斜杠）
                { path: 'index', component: () => import('./components/admin/AdminHome.vue') },
                { path: 'user', component: () => import('./components/admin/AdminUser.vue') },
                { path: 'record', component: () => import('./components/admin/AdminRecord.vue') },
                { path: 'comment', component: () => import('./components/admin/AdminComment.vue') }
            ]
        }
    ]  // 补全 routes 数组的闭合括号
})

export default router;
